import React, { useState } from 'react';
import './App.css';
import drugsData from './drugs';  // Import the drug data

function App() {
  const [currentDrugIndex, setCurrentDrugIndex] = useState(0);
  const [history, setHistory] = useState([]); // Stack to store navigation history
  const [activeDrugs, setActiveDrugs] = useState(drugsData); // Active list of drugs being studied
  const [completedDrugs, setCompletedDrugs] = useState([]); // List of completed drugs

  // Handle progressing to the next drug
  const handleNext = () => {
    if (activeDrugs.length > 1) {
      setCompletedDrugs((prev) => [...prev, activeDrugs[currentDrugIndex]]);
      setHistory((prevHistory) => [...prevHistory, currentDrugIndex]);
      setCurrentDrugIndex((prev) => (prev + 1) % activeDrugs.length);
    } else {
      alert("You have excluded all drugs or completed the study session.");
    }
  };

  // Handle going back to the previous drug
  const handleBack = () => {
    if (history.length > 0) {
      const previousIndex = history[history.length - 1];
      setHistory((prevHistory) => prevHistory.slice(0, -1)); // Remove the last entry from history
      setCurrentDrugIndex(previousIndex); // Go back to the previous drug
    } else {
      alert("No more previous drugs.");
    }
  };

  // Pronounce a drug name
  const handlePronounce = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    speechSynthesis.speak(utterance);
  };

  // Exclude the current drug from the study session
  const handleExclude = () => {
    const updatedDrugs = activeDrugs.filter((_, index) => index !== currentDrugIndex);
    setCompletedDrugs((prev) => [...prev, activeDrugs[currentDrugIndex]]);
    setActiveDrugs(updatedDrugs);
    setCurrentDrugIndex(0); // Reset index to the start of the updated list
  };

  if (activeDrugs.length === 0) {
    return <div className="App"><h1>No more drugs to study!</h1></div>;
  }

  const currentDrug = activeDrugs[currentDrugIndex];

  return (
    <div className="App">
      <h1>Drug Memorization App</h1>

      {/* Quiz Box */}
      <div className="quiz-box">
        <h2>Brand Name: {currentDrug.brand}</h2>
        <h3>Generic Name: {currentDrug.generic}</h3>
        <h4>Category: {currentDrug.category}</h4>
        <button onClick={() => handlePronounce(currentDrug.brand)}>Pronounce Brand</button>
        <button onClick={() => handlePronounce(currentDrug.generic)}>Pronounce Generic</button>
        <button onClick={handleNext}>Next Drug</button>
        <button onClick={handleBack}>Go Back</button>
        <button onClick={handleExclude}>Exclude Drug</button>
      </div>

      {/* Progress Box */}
      <div className="progress-box">
        <h3>Progress: {completedDrugs.length} / {drugsData.length}</h3>
      </div>

      {/* Progress List */}
      <div className="progress-list">
        <h3>Study Progress</h3>
        <ul>
          {drugsData.map((drug, index) => (
            <li
              key={index}
              style={{
                textDecoration: completedDrugs.includes(drug) ? 'line-through' : 'none',
                fontWeight: currentDrugIndex === index ? 'bold' : 'normal',
                color: completedDrugs.includes(drug) ? 'gray' : 'black'
              }}
            >
              {drug.brand} ({drug.generic})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
