import React, { useState } from 'react';
import './App.css';
import drugs from './drugs';  // Import the drug data

function App() {
  const [currentDrug, setCurrentDrug] = useState(0);
  const [showGeneric, setShowGeneric] = useState(false);
  const [correctCount, setCorrectCount] = useState(0);
  const [totalCount, setTotalCount] = useState(0);

  const handleNext = () => {
    setShowGeneric(false);
    setCurrentDrug((prev) => (prev + 1) % drugs.length);
  };

  const handlePronounce = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    speechSynthesis.speak(utterance);
  };

  const handleGuess = (isCorrect) => {
    if (isCorrect) setCorrectCount(correctCount + 1);
    setTotalCount(totalCount + 1);
    handleNext();
  };

  return (
    <div className="App">
      <h1>Drug Memorization App</h1>
      <div className="quiz-box">
        <h2>Brand Name: {drugs[currentDrug].brand}</h2>
        <h3>Category: {drugs[currentDrug].category}</h3>
        {showGeneric && <h3>Generic Name: {drugs[currentDrug].generic}</h3>}
        <button onClick={() => setShowGeneric(true)}>Show Generic</button>
        <button onClick={() => handlePronounce(drugs[currentDrug].brand)}>Pronounce Brand</button>
        <button onClick={() => handlePronounce(drugs[currentDrug].generic)}>Pronounce Generic</button>
        <button onClick={handleNext}>Next Drug</button>
      </div>
      <div className="guess-box">
        <h3>Did you guess the correct generic name?</h3>
        <button onClick={() => handleGuess(true)}>Correct</button>
        <button onClick={() => handleGuess(false)}>Incorrect</button>
      </div>
      <div className="progress-box">
        <h3>Progress</h3>
        <p>Correct: {correctCount} / {totalCount}</p>
      </div>
    </div>
  );
}

export default App;
